import React from "react";
import { useCookies, setCookie } from "react-cookie";

let setTokensFromUrl = async () => {
  const search = window.location.search.substring(1);
  const query = JSON.parse(
    '{"' +
      decodeURI(search)
        .replace(/"/g, '\\"')
        .replace(/&/g, '","')
        .replace(/=/g, '":"') +
      '"}'
  );

  if (query.code) {
    return fetch(
      `https://www.strava.com/oauth/token?client_id=41833&client_secret=3017d913766ccf81c8d716c0a40a961b35488aea&code=${query.code}&grant_type=authorization_code`,
      {
        method: "POST", // or 'PUT'
        headers: {
          "Content-Type": "application/json"
        }
      }
    )
      .then(response => response.json())
      .then(data => {
        console.log("Success:", data);
      })
      .catch(error => {
        console.error("Error:", error);
      });
  }

  let tokens = {
    accessToken: query.access_token,
    firebaseToken: query.firebase_token,
    refreshToken: query.refresh_token,
    expiresAt: query.expires_at
  };

  return tokens;
};

const HandleRedirect = () => {
  const [cookies, setCookie] = useCookies([]);

  React.useEffect(() => {
    setTokensFromUrl().then(value => {
      setCookie("access_token", value.accessToken, {
        path: "/",
        maxAge: 604800
      });
      setCookie("firebase_token", value.firebaseToken, {
        path: "/",
        maxAge: 604800
      });
      setCookie("refresh_token", value.refreshToken, {
        path: "/",
        maxAge: 604800
      });
      setCookie("expires_at", value.expiresAt, {
        path: "/",
        maxAge: 604800
      });
    });
  }, []);
  console.log(cookies);
  return <div>success</div>;
};

export default HandleRedirect;
