import React, { useContext, useState } from "react";
import { AuthContext } from "../App";
import { useCookies } from "react-cookie";
import { asyncFetch } from "../helpers/asyncFetch";

const metersToMiles = 0.000621371192;

export default function Dashboard() {
  const [cookies] = useCookies();
  const context = useContext(AuthContext);
  const [stats, setStats] = useState(null);
  const { access_token } = cookies;

  function AthleteStats() {
    let athleteStatsUrl = `https://www.strava.com/api/v3/athletes/${context.user.id}/stats`;
    if (access_token && !stats) {
      asyncFetch(athleteStatsUrl, cookies.access_token)
        .then(data => {
          return data.json();
        })
        .then(stats => {
          setStats(stats);
        });
      return <div>Loading Stats...</div>;
    } else {
      return (
        <React.Fragment>
          <div>
            {(stats.all_run_totals.distance * metersToMiles).toFixed(1)} miles
          </div>
          <div>
            {context.user.clubs.map(club => {
              return club.name;
            })}
          </div>
        </React.Fragment>
      );
    }
  }

  function Clubs() {
    let clubs = context.user.clubs;
    console.log(clubs);
    let clubDetails = clubs.map(club => {
      let clubDetailsUrl = `https://www.strava.com/api/v3/clubs/${club.id}`;
      return asyncFetch(clubDetailsUrl, cookies.access_token).then(data => {
        return data.json();
      });
    });
    console.log("clubDetails", clubDetails);
    return "Clubs";
  }

  if (!context.user) {
    return "Athlete not found.";
  }
  return (
    <div>
      <div>{`${context.user.firstname}${" "}${context.user.lastname}`}</div>
      <div>{`${context.user.city}${", "}${context.user.state}`}</div>
      <AthleteStats />
      <Clubs />
    </div>
  );
}
