import React, { useContext, useState } from "react";
import { AuthContext } from "../App";
import { useCookies } from "react-cookie";
import { asyncFetch } from "../helpers/asyncFetch";

const metersToMiles = 0.000621371192;

export default function Dashboard() {
  const [cookies] = useCookies();
  const context = useContext(AuthContext);
  const [stats, setStats] = useState(null);
  const { access_token } = cookies;

  function AthleteStats() {
    let athleteStatsUrl = `https://www.strava.com/api/v3/athletes/${context.user.id}/stats`;
    if (access_token && !stats) {
      asyncFetch(athleteStatsUrl, cookies.access_token)
        .then(data => {
          return data.json();
        })
        .then(stats => {
          setStats(stats);
        });
      return <div>Loading Stats...</div>;
    } else {
      return (
        <React.Fragment>
          <div>
            {(stats.all_run_totals.distance * metersToMiles).toFixed(1)} miles
          </div>
          <div>
            {context.user.clubs.map(club => {
              return club.name;
            })}
          </div>
        </React.Fragment>
      );
    }
  }

  function ClubMembers({ id }) {
    let clubMembersUrl = `https://www.strava.com/api/v3/clubs/${id}/members`;
    if (access_token && !stats) {
      asyncFetch(clubMembersUrl, cookies.access_token)
        .then(data => {
          return data.json();
        })
        .then(members => {
          return members;
        });
      return <div>Loading Club Members...</div>;
    } else {
      return "members";
    }
  }

  if (!context.user) {
    return "Athlete not found.";
  }
  return (
    <div>
      <div>{`${context.user.firstname}${" "}${context.user.lastname}`}</div>
      <div>{`${context.user.city}${", "}${context.user.state}`}</div>
      <AthleteStats />
      <div>
        {context.user.clubs.map(club => {
          return <ClubMembers id={club.id} />;
        })}
      </div>
    </div>
  );
}
